// --- lunar_log/formatter/human_readable_formatter.hpp ---

#include <string>

namespace minta {
    class HumanReadableFormatter : public IFormatter {
    public:
        std::string format(const LogEntry &entry) const override {
            std::string result;
            result.reserve(80 + entry.message.size() + entry.file.size() + entry.function.size());
            result += detail::formatTimestamp(entry.timestamp);
            result += " [";
            result += getLevelString(entry.level);
            result += "] ";
            result += entry.message;

            if (!entry.file.empty()) {
                result += " [";
                result += entry.file;
                result += ':';
                result += std::to_string(entry.line);
                result += ' ';
                result += entry.function;
                result += ']';
            }

            if (!entry.customContext.empty()) {
                result += " {";
                bool first = true;
                for (const auto &ctx : entry.customContext) {
                    if (!first) result += ", ";
                    result += ctx.first;
                    result += '=';
                    // Quote values containing delimiters
                    if (ctx.second.find(',') != std::string::npos ||
                        ctx.second.find('=') != std::string::npos ||
                        ctx.second.find('"') != std::string::npos) {
                        result += '"';
                        for (char c : ctx.second) {
                            if (c == '"') result += '\\';
                            result += c;
                        }
                        result += '"';
                    } else {
                        result += ctx.second;
                    }
                    first = false;
                }
                result += '}';
            }

            return result;
        }
    };
} // namespace minta


// --- lunar_log/formatter/json_formatter.hpp ---

#include <string>
#include <cstdio>
#include <cstdlib>
#include <cerrno>
#include <cmath>

namespace minta {
    class JsonFormatter : public IFormatter {
    public:
        std::string format(const LogEntry &entry) const override {
            std::string levelStr = getLevelString(entry.level);
            std::string tsStr = detail::formatTimestamp(entry.timestamp);
            std::string msgEsc = escapeJsonString(entry.message);

            std::string json;
            json.reserve(64 + levelStr.size() + tsStr.size() + msgEsc.size());
            json += R"({"level":")";
            json += levelStr;
            json += R"(","timestamp":")";
            json += tsStr;
            json += R"(","message":")";
            json += msgEsc;
            json += '"';

            if (!entry.templateStr.empty()) {
                json += R"(,"messageTemplate":")";
                json += escapeJsonString(entry.templateStr);
                json += R"(","templateHash":")";
                json += detail::toHexString(entry.templateHash);
                json += '"';
            }

            if (!entry.file.empty()) {
                std::string fileEsc = escapeJsonString(entry.file);
                std::string funcEsc = escapeJsonString(entry.function);
                json += R"(,"file":")";
                json += fileEsc;
                json += R"(","line":)";
                json += std::to_string(entry.line);
                json += R"(,"function":")";
                json += funcEsc;
                json += '"';
            }

            if (!entry.customContext.empty()) {
                json += R"(,"context":{)";
                bool first = true;
                for (const auto &ctx : entry.customContext) {
                    if (!first) json += ',';
                    json += '"';
                    json += escapeJsonString(ctx.first);
                    json += R"(":")";
                    json += escapeJsonString(ctx.second);
                    json += '"';
                    first = false;
                }
                json += '}';
            }

            if (!entry.properties.empty()) {
                json += R"(,"properties":{)";
                bool first = true;
                for (const auto &prop : entry.properties) {
                    if (!first) json += ',';
                    json += '"';
                    json += escapeJsonString(prop.name);
                    json += R"(":)";
                    if (prop.op == '@') {
                        json += toJsonNativeValue(prop.value);
                    } else {
                        json += '"';
                        json += escapeJsonString(prop.value);
                        json += '"';
                    }
                    first = false;
                }
                json += '}';
            }

            json += '}';
            return json;
        }

    private:
        /// Attempt to emit a JSON-native value for @ (destructure) properties.
        /// Values arrive as strings (post-toString conversion), so original type
        /// info is lost. This function uses string-based heuristics: "true"/"false"
        /// become JSON booleans, numeric-looking strings become JSON numbers.
        /// Known limitation: a string argument "true" becomes boolean true, and
        /// "3.14" becomes number 3.14.  Use the $ (stringify) operator to force
        /// string representation when this coercion is undesirable.
        /// Note: nullptr/"(null)" is emitted as the string "(null)", not JSON null,
        /// since the MessageTemplates spec does not mandate null handling.
        static std::string toJsonNativeValue(const std::string &value) {
            if (value == "true" || value == "false") {
                return value;
            }

            if (value.empty()) {
                return "\"\"";
            }

            const char* start = value.c_str();
            char* end = nullptr;
            errno = 0;
            double numVal = std::strtod(start, &end);
            if (errno == 0 && end != start && static_cast<size_t>(end - start) == value.size()
                && std::isfinite(numVal)) {
                // Re-serialize from the parsed double to guarantee valid JSON.
                // strtod accepts inputs like "+42", " 42", "0x1A" which are
                // NOT valid JSON numbers, so we cannot return the original string.
                char buf[64];
                if (numVal == static_cast<double>(static_cast<long long>(numVal))
                    && std::fabs(numVal) < 1e15) {
                    std::snprintf(buf, sizeof(buf), "%lld", static_cast<long long>(numVal));
                } else {
                    std::snprintf(buf, sizeof(buf), "%.15g", numVal);
                }
                // Locale-safety: some locales use ',' as decimal separator.
                // Replace with '.' to guarantee valid JSON numbers.
                for (char *p = buf; *p; ++p) { if (*p == ',') *p = '.'; }
                return std::string(buf);
            }
            errno = 0;

            std::string result = "\"";
            result += escapeJsonString(value);
            result += '"';
            return result;
        }

        static std::string escapeJsonString(const std::string &input) {
            std::string result;
            result.reserve(input.size());
            for (char c : input) {
                switch (c) {
                    case '"': result += R"(\")"; break;
                    case '\\': result += R"(\\)"; break;
                    case '\b': result += R"(\b)"; break;
                    case '\f': result += R"(\f)"; break;
                    case '\n': result += R"(\n)"; break;
                    case '\r': result += R"(\r)"; break;
                    case '\t': result += R"(\t)"; break;
                    default:
                        if ('\x00' <= c && c <= '\x1f') {
                            char buf[8];
                            std::snprintf(buf, sizeof(buf), "\\u%04x", static_cast<unsigned char>(c));
                            result += buf;
                        } else {
                            result += c;
                        }
                }
            }
            return result;
        }
    };
} // namespace minta


// --- lunar_log/formatter/xml_formatter.hpp ---

#include <string>
#include <cstdio>

namespace minta {
    class XmlFormatter : public IFormatter {
    public:
        std::string format(const LogEntry &entry) const override {
            std::string xml;
            xml += "<log_entry>";
            xml += "<level>";
            xml += getLevelString(entry.level);
            xml += "</level>";
            xml += "<timestamp>";
            xml += detail::formatTimestamp(entry.timestamp);
            xml += "</timestamp>";
            xml += "<message>";
            xml += escapeXmlString(entry.message);
            xml += "</message>";

            if (!entry.templateStr.empty()) {
                xml += "<MessageTemplate hash=\"";
                xml += detail::toHexString(entry.templateHash);
                xml += "\">";
                xml += escapeXmlString(entry.templateStr);
                xml += "</MessageTemplate>";
            }

            if (!entry.file.empty()) {
                xml += "<file>";
                xml += escapeXmlString(entry.file);
                xml += "</file>";
                xml += "<line>";
                xml += std::to_string(entry.line);
                xml += "</line>";
                xml += "<function>";
                xml += escapeXmlString(entry.function);
                xml += "</function>";
            }

            if (!entry.customContext.empty()) {
                xml += "<context>";
                for (const auto &ctx : entry.customContext) {
                    std::string safeName = sanitizeXmlName(ctx.first);
                    xml += "<";
                    xml += safeName;
                    xml += ">";
                    xml += escapeXmlString(ctx.second);
                    xml += "</";
                    xml += safeName;
                    xml += ">";
                }
                xml += "</context>";
            }

            if (!entry.properties.empty()) {
                xml += "<properties>";
                for (const auto &prop : entry.properties) {
                    std::string safeName = sanitizeXmlName(prop.name);
                    xml += "<";
                    xml += safeName;
                    if (prop.op == '@') {
                        xml += " destructure=\"true\"";
                    } else if (prop.op == '$') {
                        xml += " stringify=\"true\"";
                    }
                    xml += ">";
                    xml += escapeXmlString(prop.value);
                    xml += "</";
                    xml += safeName;
                    xml += ">";
                }
                xml += "</properties>";
            }

            xml += "</log_entry>";
            return xml;
        }

    private:
        static std::string sanitizeXmlName(const std::string &input) {
            if (input.empty()) return "_";
            std::string result;
            result.reserve(input.size());
            for (size_t i = 0; i < input.size(); ++i) {
                char c = input[i];
                bool valid = (c == '_' || c == ':') ||
                             (c >= 'A' && c <= 'Z') ||
                             (c >= 'a' && c <= 'z') ||
                             (i > 0 && ((c >= '0' && c <= '9') || c == '-' || c == '.'));
                result += valid ? c : '_';
            }
            // Safety net: the loop above already replaces invalid start chars with '_',
            // so this check for a leading digit/dash/dot is unreachable with the
            // current logic. Kept as defensive validation in case the loop changes.
            if (result.empty() || result[0] == '-' || result[0] == '.' || (result[0] >= '0' && result[0] <= '9')) {
                result.insert(result.begin(), '_');
            }
            return result;
        }

        static std::string escapeXmlString(const std::string &input) {
            std::string result;
            result.reserve(input.size());
            for (char c : input) {
                unsigned char uc = static_cast<unsigned char>(c);
                if (uc < 0x20 && uc != 0x09 && uc != 0x0A && uc != 0x0D) {
                    result += ' ';
                    continue;
                }
                switch (c) {
                    case '<': result += "&lt;"; break;
                    case '>': result += "&gt;"; break;
                    case '&': result += "&amp;"; break;
                    case '\'': result += "&apos;"; break;
                    case '"': result += "&quot;"; break;
                    default: result += c; break;
                }
            }
            return result;
        }
    };
} // namespace minta


